    public func send<Operation>(operation: Operation, completionHandler: @escaping (Result<GraphQLResponse<Operation.Data>, Error>) -> Void) -> Cancellable where Operation: GraphQLOperation {
        let query = requestBody(for: operation)
        guard var urlComponents = URLComponents(url: url, resolvingAgainstBaseURL: false) else {
            completionHandler(.failure(NetworkError.invalidURLComponents))
            return EmptyCancellable()
        }
        urlComponents.queryItems = query.compactMap { queryMapItem -> URLQueryItem? in
            guard let queryMapItemValue = queryMapItem.value else { return nil }
            if let queryMapItemValue = queryMapItemValue as? JSONObject {
                guard let queryItemValue = ((try? String(data: JSONSerializationFormat.serialize(value: queryMapItemValue), encoding: .utf8)) as String??) else { return nil }
                return URLQueryItem(name: queryMapItem.key, value: queryItemValue)
            }
            if let queryMapItemValue = queryMapItemValue as? String {
                return URLQueryItem(name: queryMapItem.key, value: queryMapItemValue)
            }
            return nil
        }

        var request = URLRequest(url: url)
        request.setValue("application/json", forHTTPHeaderField: "Content-Type")
        request.httpMethod = "GET"
        request.url = urlComponents.url
        let task = session.dataTask(with: request) { (data: Data?, response: URLResponse?, error: Swift.Error?) in
            if let error = error {
                completionHandler(.failure(error))
                return
            }

            guard let httpResponse = response as? HTTPURLResponse else {
                fatalError("Response should be an HTTPURLResponse")
            }

            if !httpResponse.isSuccessful {
                completionHandler(.failure(GraphQLHTTPResponseError(body: data, response: httpResponse, kind: .errorResponse)))
                return
            }

            guard let data = data else {
                completionHandler(.failure(GraphQLHTTPResponseError(body: nil, response: httpResponse, kind: .invalidResponse)))
                return
            }

            do {
                guard let body = try self.serializationFormat.deserialize(data: data) as? JSONObject else {
                    throw GraphQLHTTPResponseError(body: data, response: httpResponse, kind: .invalidResponse)
                }
                let response = GraphQLResponse(operation: operation, body: body)
                completionHandler(.success(response))
            } catch {
                completionHandler(.failure(error))
            }
        }

        task.resume()

        return task
    }

    private let sendOperationIdentifiers: Bool

    private func requestBody<Operation: GraphQLOperation>(for operation: Operation) -> GraphQLMap {
        if sendOperationIdentifiers {
            guard let operationIdentifier = operation.operationIdentifier else {
                preconditionFailure("To send operation identifiers, Apollo types must be generated with operationIdentifiers")
            }
            return ["id": operationIdentifier, "variables": operation.variables]
        }
        return ["query": operation.queryDocument, "variables": operation.variables]
    }
}

